mapper.select:
select count(*)
from HRO_SEC_Group_Column_Right_Relation
where deleted = 1 AND status = 1 AND groupId = #{groupId} 

	AND columnId = #{columnId}


	AND rightId = #{rightId}

select relationId, 
from HRO_SEC_Group_Column_Right_Relation
where deleted = 1 AND status = 1 AND groupId = #{groupId} 

	AND columnId = #{columnId}


	AND rightId = #{rightId}


	order by ${sortColumn} ${sortOrder}

select relationId, 
from HRO_SEC_Group_Column_Right_Relation
where groupId = #{value}

select relationId, 
from HRO_SEC_Group_Column_Right_Relation
where relationId = #{value}

mapper.update:
update HRO_SEC_Group_Column_Right_Relation set groupId = #{groupId}, columnId = #{columnId}, rightId = #{rightId}, deleted = #{deleted}, 
	status = #{status}, remark1 = #{remark1}, remark2 = #{remark2}, remark3 = #{remark3}, remark4 = #{remark4}, remark5 = #{remark5}, 
	modifyBy = #{modifyBy}, modifyDate = #{modifyDate}
where relationId = #{relationId}
