SELECT COUNT(DISTINCT a.headerId) FROM hro_cb_header a LEFT JOIN hro_cb_detail c ON a.headerId = c.headerId AND c.deleted = 1 LEFT JOIN hro_biz_employee_contract_cb d ON a.employeeCBId = d.employeeCBId AND d.deleted = 1 LEFT JOIN hro_biz_employee e ON a.employeeId = e.employeeId AND e.deleted = 1 LEFT JOIN hro_biz_client f ON f.clientId = a.clientId AND f.deleted = 1 LEFT JOIN hro_mgt_item g ON c.itemId = g.itemId AND g.deleted = 1 WHERE a.deleted = 1 AND a.accountId = #{accountId}
AND (a.corpId = #{corpId} OR 0 = #{corpId})
AND a.corpId IS NULL
AND (a.batchId = #{batchId} or 0 = #{batchId})
AND (a.contractId = #{contractId} or 0 = #{contractId})
AND a.employeeId = #{employeeId}
AND e.employeeNo = #{employeeNo}
AND a.certificateType = #{certificateType}
AND a.certificateNumber = #{certificateNumber}
AND (a.residencyType = #{residencyType} or '0' = #{residencyType})
AND a.employeeNameZH like CONCAT('%', '${employeeNameZH}', '%')
AND a.employeeNameEN like CONCAT('%', '${employeeNameEN}', '%')
AND (a.employeeCBId = #{employeeCBId} or '0' = #{employeeCBId})
AND (d.solutionId = #{cbId} or '0' = #{cbId})
AND (a.monthly = #{monthly} or '0' = #{monthly})
AND a.monthly <= #{monthlyLimit}
AND (c.status in (${status}) or '0' = '${status}')
AND a.cbStatus IN (${cbStatus})
AND a.clientId = ${clientId}
AND (c.status in (3,4,5))

SELECT DISTINCT a.headerId, , COUNT(1) AS countItemId, MIN(c.status) as additionalStatus, d.solutionId AS cbId, f.number AS clientNumber, d.cbNumber AS cbNumber, d.status AS contractStatus, SUM(c.amountSalesCost) AS amountSalesCost, SUM(c.amountSalesPrice) AS amountSalesPrice FROM hro_cb_header a LEFT JOIN hro_cb_detail c ON a.headerId = c.headerId AND c.deleted = 1 LEFT JOIN hro_biz_employee_contract_cb d ON a.employeeCBId = d.employeeCBId AND d.deleted = 1 LEFT JOIN hro_biz_employee e ON a.employeeId = e.employeeId AND e.deleted = 1 LEFT JOIN hro_biz_client f ON f.clientId = a.clientId AND f.deleted = 1 LEFT JOIN hro_mgt_item g ON c.itemId = g.itemId AND g.deleted = 1 WHERE a.deleted = 1 AND a.accountId = #{accountId}
AND (a.corpId = #{corpId} OR 0 = #{corpId})
AND a.corpId IS NULL
AND (a.batchId = #{batchId} or 0 = #{batchId})
AND (a.contractId = #{contractId} or 0 = #{contractId})
AND a.employeeId = #{employeeId}
AND e.employeeNo = #{employeeNo}
AND (a.residencyType = #{residencyType} or '0' = #{residencyType})
AND a.certificateNumber = #{certificateNumber}
AND a.employeeNameZH like CONCAT('%', '${employeeNameZH}', '%')
AND a.employeeNameEN like CONCAT('%', '${employeeNameEN}', '%')
AND (a.employeeCBId = #{employeeCBId} or '0' = #{employeeCBId})
AND (d.solutionId = #{cbId} or '0' = #{cbId})
AND (a.monthly = #{monthly} or '0' = #{monthly})
AND a.monthly <= #{monthlyLimit}
AND (c.status in (${status}) or '0' = '${status}')
AND a.cbStatus IN (${cbStatus})
AND a.clientId = ${clientId}
AND (c.status in (3,4,5))
GROUP BY a.headerId
order by ${sortColumn} ${sortOrder}

