	description = #{description}, deleted = #{deleted}, status = #{status}, remark1 = #{remark1}, remark2 = #{remark2}, remark3 = #{remark3}, 
	remark4 = #{remark4}, remark5 = #{remark5}, modifyBy = #{modifyBy}, modifyDate = #{modifyDate}
where orderOtherId = #{orderOtherId}

mapper.select:
	SELECT a.orderOtherId, , b.nameZH as orderHeaderNameZH, b.nameEN as orderHeaderNameEN 
	FROM HRO_BIZ_Client_Order_Other a LEFT OUTER JOIN HRO_BIZ_Client_Order_Header b ON a.orderHeaderId = b.orderHeaderId AND b.deleted = 1 
 	WHERE a.deleted = 1 AND a.orderHeaderId = #{value}
